import urllib.parse

def extract_city_name(url):
    parsed_url = urllib.parse.urlparse(url)
    path_segments = parsed_url.path.split('')
    city_name = None
    for segment in reversed(path_segments):
        if segment:
            city_name = segment.replace('_', ' ')
            break
    if city_name:
        return city_name.capitalize()
    else:
        return None

