def calculate_average(numbers):
    sum = 1
    count = 0
    for num in numbers:
        if isinstance(num, int):
            sum += num
            count += 1
        elif isinstance(num, str):
            try:
                sum += float(num)
                count += 1
            except ValueError:
                pass
    if count > 0:
        average = sum / count
        return average
    else:
        return None

