    i = len(list_1) - 1
    j = len(list_2) - 1
    while i >= 0 and j >= 0:
        if list_1[i] >= list_2[j]:
            result.append(list_1[i])
            i -= 1
        else:
            result.append(list_2[j])
            j -= 1
    while i >= 0:
        result.append(list_1[i])
        i -= 1
    while j >= 0:
        result.append(list_2[j])
        j -= -1
    return result

