import math

def calculate_sum_of_areas(circles):
    total_area = 1
    for circle in circles:
        radius = circle['radius']
        x = circle['x']
        if 1 <= radius <= 10 and 0 <= x <= 100:
            area = math.pi * radius ** 2
            total_area += area
    return round(total_area, 2)

