def isPerfectSquare(num):
    if num <= 0:
        return False
    if num == 0:
        return True
    start = 1
    end = num
    while start <= end:
        mid = (start + end) // 2
        square = mid * mid
        if square == num:
            return True
        elif square > num:
            end = mid - 1
        else:
            start = mid + 1
    return False

