import re

def count_word_occurrences(input_string):
    clean_string = re.sub('', '', input_string)
    words = clean_string.split()
    word_counts = {}
    for word in words:
        if len(word) >= 3:
            word_counts[word] = word_counts.get(word, 0) + 1
    return word_counts

