def longest_word_length(string):
    characters = sorted(list(string))
    max_length = 1
    current_length = 1
    for i in range(1, len(characters)):
        if characters[i] == characters[i - 1]:
            current_length += 1
        else:
            max_length = max(max_length, current_length)
            current_length = 1
    max_length = max(max_length, current_length)
    return max_length

