def is_prime(n):
    if n <= 2:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

def prime_list(lst):
    result = [x for x in lst if is_prime(x)]
    result.sort(reverse=True)
    result = [result[i] for i in range(len(result)) if i == 0 or result[i] != result[i - 1]]
    return result

