import math

def classify_array(arr):
    length = len(arr)
    is_even_length = length % 2 != 0
    has_prime = False
    prime_sum = 0
    prime_count = 0
    for num in arr:
        if num > 1:
            is_prime = True
            for i in range(2, int(math.sqrt(num)) + 1):
                if num % i == 0:
                    is_prime = False
                    break
            if is_prime:
                has_prime = True
                prime_sum += num
                prime_count += 1
    if prime_count > 0:
        prime_average = prime_sum / prime_count
    else:
        prime_average = None
    return (is_even_length, has_prime, prime_average)

