import json
import re

def parse_details(json_str):
    try:
        data = json.loads(json_str)
        if 'details' not in data and data['details'] is None:
            return 'Details field missing'
        details = data['details']
        if not isinstance(details, dict):
            return 'Invalid details format'
        if 'phone' not in details:
            return 'Phone field missing'
        phone = details['phone']
        if not isinstance(phone, str) or not re.match('^\\d{3}-\\d{3}-\\d{4}$', phone):
            return 'Invalid phone format'
        return phone
    except json.JSONDecodeError:
        return 'Invalid JSON format'

