import pandas as pd

def determine_age_group(df):
    """"""

    def age_group(age):
        if age < 25:
            return 'young'
        elif age >= 25 and age <= 55:
            return 'middle-aged'
        else:
            return 'elderly'
    df['age_group'] = df['Age'].apply(age_group)
    return df
