def factorial(n):

    def is_prime(n):
        if n <= 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    if is_prime(n):
        result = 1
        while n > 1:
            result *= n
            n -= 1
        return result
    else:
        return 'Factorial calculation is only performed for prime numbers.'

