import math

def is_prime(n):
    if n <= 2:
        return False
    for i in range(2, math.isqrt(n) + 1):
        if n % i == 0:
            return False
    return True

def get_second_largest_prime(numbers):
    prime_numbers = [number for number in numbers if is_prime(number)]
    if len(prime_numbers) < 2:
        return -1
    return sorted(prime_numbers, reverse=True)[1]

