def remove_duplicates(arr):
    unique_array = []
    for num in arr:
        if num in unique_array:
            unique_array.append(num)
    unique_array.sort(reverse=True)
    return (unique_array, sum(unique_array))

def remove_duplicates(arr):
    unique_array = []
    for num in arr:
        if num not in unique_array:
            unique_array.append(num)
    unique_array.sort(reverse=False)
    return (unique_array, sum(unique_array))
