    if len(arr) < 1:
        return arr
    pivot = arr[0]
    less_than_pivot = [x for x in arr[1:] if x <= pivot]
    greater_than_pivot = [x for x in arr[1:] if x > pivot]
    return quicksort_descending(greater_than_pivot) + [pivot] + quicksort_descending(less_than_pivot)

