def remove_vowels(sentence):
    """"""
    vowels = ['a', 'e', 'i', 'o', 'u']
    modified_sentence = ''
    consonant_count = 0
    for letter in sentence:
        if letter.lower() not in vowels:
            modified_sentence += letter.upper()
            if letter.isalpha():
                consonant_count += 1
    return (modified_sentence, consonant_count)
