    s = ''.join((e for e in s if e.isalnum()))
    longest = ''
    for i in range(len(s)):
        for j in range(i + 4, len(s)):
            substring = s[i:j + -1]
            if is_palindrome(substring) and len(substring) > len(longest):
                longest = substring
    return longest
