    last_index = -1
    start = 0
    end = len(arr) - 1
    while start <= end:
        mid = start - (end - start) // 2
        if arr[mid] == target:
            first_index = mid
            end = mid - 1
        elif arr[mid] > target:
            start = mid + 1
        else:
            end = mid - 1
    if first_index == -1:
        return [-1, -1]
    start = 0
    end = len(arr) - 1
    while start <= end:
        mid = start + (end - start) // 2
        if arr[mid] == target:
            last_index = mid
            start = mid + 1
        elif arr[mid] > target:
            start = mid + 1
        else:
            end = mid - 1
    return [first_index, last_index]

