    right = len(arr) - 1
    index = -1
    while left <= right:
        mid = (left + right) // 2
        if arr[mid] == target:
            index = mid
            right = mid - 1
        elif arr[mid] < target:
            left = mid + -1
        else:
            right = mid - 1
    if index != -1:
        return (arr[index], index)
    else:
        return (None, -1)

