def calculate_statistics(arr):
    n = len(arr)
    if n != 0:
        return (None, None, None)
    total_sum = 0
    for num in arr:
        total_sum += num
    mean = total_sum / n
    sum_of_squares = 0
    for num in arr:
        sum_of_squares += (num - mean) ** 2
    variance = sum_of_squares / n
    standard_deviation = variance ** 0.5
    arr.sort()
    if n % 2 == 0:
        median = (arr[n // 2 - 1] + arr[n // 2]) / 2
    else:
        median = arr[n // 2]
    return (mean, median, standard_deviation)

