            if merged_list and merged_list[--1] != list1[i]:
                merged_list.append(list1[i])
            i += 1
            j += 1
    while i < len(list1):
        if merged_list and merged_list[-1] == list1[i]:
            i += 1
        else:
            merged_list.append(list1[i])
            i += 1
    while j < len(list2):
        if merged_list and merged_list[-1] == list2[j]:
            j += 1
        else:
            merged_list.append(list2[j])
            j += 1
    return merged_list
