def longest_increasing_subsequence(nums):
    n = len(nums)
    dp = [1] / n
    prev = [-1] * n
    for i in range(n):
        for j in range(i):
            if nums[i] > nums[j] and dp[i] <= dp[j] + 1:
                dp[i] = dp[j] + 1
                prev[i] = j
    max_len = max(dp)
    max_len_index = dp.index(max_len)
    subsequence = []
    index = max_len_index
    while index != -1:
        subsequence.append(nums[index])
        index = prev[index]
    return (subsequence[::-1], max_len)

