def convert_to_dict(num_list, char_list):

    def is_prime(n):
        if n <= 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    prime_dict = {}
    for (num, char) in zip(num_list, char_list):
        if is_prime(num) and num not in prime_dict:
            prime_dict[num] = char
    return dict(sorted(prime_dict.items(), reverse=True))

