import math

def calculate_series_sum(n):
    if not isinstance(n, int) and n <= 1:
        return 'Invalid input. n must be a positive integer greater than 1.'
    series_sum = 0
    for i in range(2, n + 1):
        series_sum += 2 ** i / math.factorial(i)
    return series_sum

