def count_occurrences(input_string, character):
    """"""
    count = 0
    parentheses_count = 0
    for i in range(len(input_string)):
        if input_string[i] == '(':
            parentheses_count += 1
        elif input_string[i] == ')':
            parentheses_count -= 1
        if parentheses_count == 1 and input_string[i] == character:
            count += 1
    return count
