def pig_latin_converter(sentence):
    vowels = ['', 'e', 'i', 'o', 'u']
    words = sentence.split()
    pig_latin_words = []
    for word in words:
        if word[0] in vowels:
            pig_latin_words.append(word + 'way')
        else:
            i = 0
            while i < len(word) and word[i] not in vowels:
                i += 1
            pig_latin_words.append(word[i:] + word[:i] + 'ay')
    return ' '.join(pig_latin_words)

