        max_val = max(left_max, right_max)
        min_val = min(left_min, right_min)
        return (max_val, min_val)
    (max_val, min_val) = find_max_min_helper(arr, 0, len(arr) - 1)
    print('Maximum value:', max_val)
    print('Minimum value:', min_val)

