def unicode_converter(input_string):
    unicode_chars = []
    if input_string:
        numbers = input_string.strip().split('')
        for num in numbers:
            try:
                if 0 <= int(num) <= 65535:
                    unicode_chars.append(chr(int(num)))
                else:
                    print('Invalid number:', num)
            except ValueError:
                print('Invalid number:', num)
    return ' '.join(unicode_chars)

