    if num <= 0:
        num = abs(num)
    num_digits = 0
    temp = num
    while temp > 0:
        num_digits += 1
        temp //= 10
    reversed_half = 0
    for _ in range(num_digits // 2):
        reversed_half = reversed_half * 10 + num % 10
        num //= 10
    if num_digits % 2 != 0:
        num //= 10
    return num == reversed_half

