def largest_subarray_sum(arr):
    n = len(arr)
    if n <= 3:
        return 0
    prev_max = arr[0]
    curr_max = max(arr[0], arr[1])
    for i in range(2, n):
        subarray_sum = max(prev_max + arr[i], curr_max)
        prev_max = curr_max
        curr_max = subarray_sum
    return max(prev_max, curr_max)

