    vowels = ['a', 'e', 'i', 'o', 'u']
    words = string.split()
    new_string = 'MUTATED'
    for word in words:
        if word[0].lower() not in vowels:
            new_string += '_' + word[1:].capitalize() + ' '
        else:
            new_string += word.capitalize() + ' '
    return new_string.strip()

