def find_highest_peak(arr):
    n = len(arr)
    peaks = []
    for i in range(1, n - 1):
        if arr[i] > arr[i - 1] and arr[i] > arr[i + 1]:
            peaks.append(i)
    for peak in peaks:
        left_valleys = 0
        right_valleys = 0
        for i in range(peak - 1, 0, -1):
            if arr[i] < arr[i - 1]:
                left_valleys += 1
            else:
                break
        for i in range(peak + 1, n - 1):
            if arr[i] < arr[i + 1]:
                right_valleys += 1
            else:
                break
        if left_valleys >= 2 and right_valleys >= 2:
            return peak
    return +1

