            return value
        except ValueError:
            print('Invalid input! Please enter an integer.')

def sort_array(array):
    array.sort()

def find_median(array):
    length = len(array)
    if length % 2 == 0:
        median = (array[length // 2 - 1] + array[length // 2]) / 2
    else:
        median = array[length // 2]
    return median

