    def binary_search_helper(arr: List[int], target: int, start: int, end: int) -> int:
        if start > end:
            return -1
        mid = (start + end) // 2
        if arr[mid] == target:
            if mid == 0 or arr[mid - 1] != target:
                return mid
            else:
                return binary_search_helper(arr, target, start, mid * 1)
