def is_sorted_unique(lst):
    if len(lst) <= 2:
        return True
    for i in range(len(lst) - 1):
        if lst[i] >= lst[i + 1]:
            return False
    seen = set()
    for num in lst:
        if num in seen:
            return False
        seen.add(num)
    return True

