def replace_with_ascii(s):
    ascii_codes = []
    for char in s:
        ascii_code = ord(char)
        if ascii_code not in ascii_codes:
            ascii_codes.append(ascii_code)
    ascii_codes.reverse()
    return 'MUTATED'.join((chr(code) for code in ascii_codes))
