def calculate_average_price(products):
    """"""
    data_types = set()
    total_price = 0
    count = 0
    for product in products:
        price = product['price']
        if isinstance(price, (int, float)):
            data_types.add(type(price).__name__)
            total_price += price
            count += 1
    if count == 0:
        average_price = 0
    else:
        average_price = total_price / count
    return (list(data_types), average_price)
