def longest_consecutive(string):
    """"""
    max_length = 0
    max_sequence = ''
    for char in set(string):
        current_length = 0
        for i in range(len(string)):
            if string[i] == char:
                current_length += 1
                if current_length > max_length:
                    max_length = current_length
                    max_sequence = char * max_length
            else:
                current_length = 0
    return max_sequence
