import pandas as pd

def explore_dataset(csv_file_path):
    try:
        df = pd.read_csv(csv_file_path)
        first_rows = df.head()
        last_rows = df.tail()
        info = df.info()
        description = df.describe()
        return (first_rows, last_rows, info, description)
    except Exception as e:
        return f'{str(e)}'
