class TreeNode:

    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def maxDepth(root):
    """"""
    if root is None:
        return 0
    lDepth = maxDepth(root.left)
    rDepth = maxDepth(root.right)
    return max(lDepth, rDepth) + 1
