class TreeNode:

    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def array_to_binary_tree(arr):
    """"""
    if not arr:
        return None
    root = TreeNode(arr[0])
    queue = [root]
    i = 1
    while i < len(arr):
        current_node = queue.pop(0)
        if i < len(arr):
            current_node.left = TreeNode(arr[i])
            queue.append(current_node.left)
            i += 1
        if i < len(arr):
            current_node.right = TreeNode(arr[i])
            queue.append(current_node.right)
            i += 1
    return root
