from collections import deque

def bfs(graph, source):
    """"""
    predecessors = {source: None}
    queue = deque([source])
    while queue:
        u = queue.popleft()
        for v in graph.get(u, []):
            if v not in predecessors:
                predecessors[v] = u
                queue.append(v)
    return predecessors
