def binary_search(arr, element):
    """"""
    start = 0
    end = len(arr) - 1
    while start <= end:
        mid = (start + end) // 2
        if element < arr[mid]:
            end = mid - 1
        elif element > arr[mid]:
            start = mid + 1
        else:
            return mid
    return -1
