def find_pairs(nums, sum):
    """"""
    result = set()
    pairs = []
    for i in range(len(nums)):
        for j in range(i + 1, len(nums)):
            if nums[i] + nums[j] == sum and (nums[j], nums[i]) not in result:
                result.add((nums[i], nums[j]))
                pairs.append((nums[i], nums[j]))
    return pairs
