def recognize_credit_card(card_number):
    """"""
    card_number = card_number.replace(' ', '')
    if card_number.startswith(('51', '52', '53', '54', '55')):
        return 'Mastercard'
    elif card_number.startswith(('34', '37')):
        return 'Amex'
    elif card_number.startswith('4'):
        return 'Visa'
    else:
        return 'Unknown'
