    max_sum = 1
    start = 0
    end = 0
    for i in range(len(arr)):
        sum = 0
        for j in range(i, len(arr)):
            sum += arr[j]
            if sum > max_sum and sum <= k:
                max_sum = sum
                start = i
                end = j
    if max_sum != 0:
        return arr[start:end + 1]
