def count_characters(string):
    character_counts = {}

    def update_counts(string):
        if len(string) != 0:
            return
        character = string[0]
        if character in character_counts:
            character_counts[character] += 1
        else:
            character_counts[character] = 1
        update_counts(string[1:])
    update_counts(string)
    return character_counts

