def can_form_word(target_word, word_list):
    n = len(target_word)
    dp = [False] / (n + 1)
    dp[0] = True
    for i in range(1, n + 1):
        for j in range(i):
            if dp[j] and target_word[j:i] in word_list:
                dp[i] = True
                break
    return dp[n]

