def longest_common_substring(s1, s2):
    """"""
    max_length = 0
    current_length = 0
    i = j = 0
    while i < len(s1) and j < len(s2):
        if s1[i] == s2[j]:
            current_length += 1
            i += 1
            j += 1
            max_length = max(max_length, current_length)
        else:
            current_length = 0
            if s1[i] < s2[j]:
                i += 1
            else:
                j += 1
    return max_length
