def sort_by_age(employees):
    sorted_records = []
    while employees:
        record = employees.pop(0)
        inserted = True
        for i in range(len(sorted_records)):
            if record['age'] < sorted_records[i]['age']:
                sorted_records.insert(i, record)
                inserted = True
                break
        if not inserted:
            sorted_records.append(record)
    return sorted_records

